// This page contains the patient form
import React from "react";
import { useEffect, useState } from "react";

function AllPatients() {
  const [listOfForms, setListOfForms] = useState([])
  const [firstName, setFirstName] = useState("")
  const [lastName, setLastName] = useState("")

  useEffect(() => {
    // fetch always succeeds (besides network error), so need additional error checking
    fetch("http://localhost:3001/forms", {
      headers: {
        accessToken: sessionStorage.getItem("accessToken"),
      },
    })
      .then((res) => {
        if (res.ok) {
          return res.json();
        } else {
          console.log("FETCH failed");
        }
      })
      .then((data) => setListOfForms(data))
      .catch((error) => console.log("ERROR", error));
  }, []);

  function handleFirstNameChange(event) {
    setFirstName(event.target.value);
  }
  function handleLastNameChange(event) {
    setLastName(event.target.value);
  }


  function checkName(patient) {
    return patient.first_name.toLowerCase().includes(firstName.toLowerCase());
  }

  return (
    <div className="App">
      
      <input name="patient" onChange={handleFirstNameChange} placeholder='First Name' />
      <input name="patient" onChange={handleLastNameChange} placeholder='First Name' />
      {listOfForms.filter(checkName).map((patient) => (
        <h1 className="Name">{patient.first_name}</h1>
      ))}
      
      
    </div>
  );
}
/*

{listOfForms.map((value, index) => {
        return <div key={value.id}> {value.first_name} {value.last_name}</div>;
      })} 
*/

export default AllPatients;
