// This page contains the patient form
import React from "react";
import { useEffect, useState } from "react";

function AllPatients() {
  const [listOfForms, setListOfForms] = useState([]);
  const [name, setName] = useState("")

  useEffect(() => {
    // fetch always succeeds (besides network error), so need additional error checking
    fetch("http://localhost:3001/forms")
      .then((res) => {
        if (res.ok) {
          return res.json();
        } else {
          console.log("FETCH failed");
        }
      })
      .then((data) => setListOfForms(data))
      .catch((error) => console.log("ERROR", error));
  }, []);

  function handleChange(event) {
    console.log(event.target.value);
    setName(event.target.value);
  }
  function checkName(user) {
    return user.name.toLowerCase().includes(name.toLowerCase());
  }

  return (
    <div>
      <input name="name" onChange={handleChange} />

    {/*   {listOfForms.map((value, index) => {
        return <div key={value.id}> {value.first_name} {value.last_name}</div>;
      })} */}

      {listOfForms.filter(checkName).map((patient) => (
        <h1 className="Name">{user.name}</h1>
      ))}

    </div>
  );
}

export default AllPatients;
